package com.example.monitoreo

import android.content.res.Resources
import android.graphics.Bitmap
import android.graphics.BitmapFactory
import android.media.Image
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.os.Environment
import android.util.Base64
import android.widget.Button
import android.widget.ImageView
import java.io.File
import java.io.FileOutputStream
import java.util.*

class ImagenAlerta : AppCompatActivity()
{
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_imagen_alerta)
        val imageString=""
        val decodeByte = Base64.decode(imageString,Base64.DEFAULT)
        //val bitmap=BitmapFactory.decodeByteArray(decodeByte,0,decodeByte.size)
        val image_alerta=findViewById<ImageView>(R.id.image_alert)
        val bitmap=decodeSampledBitmapFromResource(decodeByte,R.id.image_alert,100,100)
        image_alerta.setImageBitmap(bitmap)
        val btnGI=findViewById<Button>(R.id.btnGI)
        btnGI.setOnClickListener {
            saveImage(bitmap)
        }
    }
    fun calculateInSampleSize(options: BitmapFactory.Options, reqWidth: Int, reqHeight: Int): Int {
        // Raw height and width of image
        val (height: Int, width: Int) = options.run { outHeight to outWidth }
        var inSampleSize = 1

        if (height > reqHeight || width > reqWidth) {

            val halfHeight: Int = height / 2
            val halfWidth: Int = width / 2

            // Calculate the largest inSampleSize value that is a power of 2 and keeps both
            // height and width larger than the requested height and width.
            while (halfHeight / inSampleSize >= reqHeight && halfWidth / inSampleSize >= reqWidth) {
                inSampleSize *= 2
            }
        }

        return inSampleSize
    }
    fun decodeSampledBitmapFromResource(
        res: ByteArray,
        resId: Int,
        reqWidth: Int,
        reqHeight: Int
    ): Bitmap {
        // First decode with inJustDecodeBounds=true to check dimensions
        return BitmapFactory.Options().run {
            inJustDecodeBounds = true
            BitmapFactory.decodeByteArray(res,0,res.size)

            // Calculate inSampleSize
            inSampleSize = calculateInSampleSize(this, reqWidth, reqHeight)

            // Decode bitmap with inSampleSize set
            inJustDecodeBounds = false

            BitmapFactory.decodeByteArray(res,0,res.size)
        }
    }
    fun saveImage(bitmap:Bitmap)
    {
        val now=Date()
        android.text.format.DateFormat.format("yyyy-MM-dd HH:mm:ss",now)
        //val mPath=Environment.getExternalStorageDirectory().toString() + "/" + now + ".jpg"
        val mPath=Environment.getDataDirectory().toString() + "/" + now + ".jpg"
        val imageFile= File(mPath)
        if(!imageFile.exists())
        {
            imageFile.createNewFile()
        }
        val outputStream=FileOutputStream(imageFile)
        val quality=100
        bitmap.compress(Bitmap.CompressFormat.JPEG,quality,outputStream)
        outputStream.flush()
        outputStream.close()
    }
}